import { useState, useEffect } from 'react';
import './Admin.css';
import AddPage from './AddPage';
import Detailspage from './Detailspage';
//import { BrowserRouter as Router,Route, Switch } from "react-router-dom";
import { db } from "./firebaseConfig";
import { collection, getDocs , addDoc, deleteDoc, doc } from "firebase/firestore";
 //import { async } from '@firebase/util';


const data =[
{id:1,name:"Funsho ajayi",sex:"M",},
{id:2,name:"Funsho ajayi",sex:"M",},
{id:3,name:"victor Kiolade",sex:"F",},
{id:4,name:"Mudia baudnsn",sex:"M",},
{id:5,name:"victor Kiolade",sex:"M",},
{id:6,name:"Mudia baudnsn",sex:"F",},
]

//getting data from database
const regCollection = collection(db, "Information ");


const Admin = () => {
//first things first
//does this onpage load    
const firebaseData = []

/* getDocs(regCollection)
    .then((snap)=>{
        snap.docs.forEach((doc) => firebaseData.push({...doc.data() , id:doc.id}))
      setIsPending(false)
    setInfo(firebaseData)
          })
    .catch(err=>alert(err.message))
    */
    
useEffect(()  => {
console.log(firebaseData)
setInfo(data) }, [])




//states
//demo array state
    const [info, setInfo] = useState(null)
//loading circle toggle state
    const [isPending, setIsPending] = useState(false)
//show full info per perdon toggle state
    const [showInfo, setShowInfo] = useState(false)
//add info manually display toggle
     const [addInfoDisplayToggle, setAddInfoDisplayToggle] = useState(false)
//states for storing new data to be pushed to the backend storage
     const [firstName, setFirstName] = useState("")
     const [lastName, setLastName] = useState("")
      const [sex, setSex] = useState("")
     const [email, setEmail] = useState("")
     const [phone, setPhone] = useState("")

//states containing info about person for full details page
const [name, setName] = useState(null)
const [sexy, setSexy] = useState(null)
const [showInfoToggle, setShowInfoToggle] = useState(false)

//mobile menu toggle
const [mobileMenuToggle, setMobileMenuToggle] = useState(false)

   let newB ;
   
//Manual add buttton, addBtn function for online
const addBtn =(e)=> {
e.preventDefault()
  
    let obj ={
        firstName:firstName,
        lastName:lastName,
        sex:sex,
        email:email,
        phone:phone,
    }  
  console.log(obj)

  if((obj.firstName !== null || "") && (obj.lastName !== null || "")  && (obj.email !== null  || "") && 
  (obj.phone !== null || ""))
  { 
    console.log("what")
  /*  addDoc(regCollection, obj)
    .then(()=>{
  if((obj.firstName !== null || "") && (obj.lastName !== null || "")  && (obj.email !== null  || "") && 
  (obj.phone !== null || ""))
  {alert("Data was added succesfully")
}
})
.catch((error)=> {alert("There was an error :"+ error)})*/
}

    addBtnClick()
}

//addBtn click
const addBtnClick =()=> setAddInfoDisplayToggle(!addInfoDisplayToggle)

//making a backup copy of the intial demo database info
const newData = data.slice()

//Show more info btn 
const showInfoBtn =(e)=> {
const id = e.target.id
let personalInfo = newData.filter(x=>x.id === id * 1)

setShowInfoToggle(true)
setName(personalInfo[0].name)
setSexy(personalInfo[0].sex)
}

//close btn for full info page
const closeInfoBtn =()=> setShowInfoToggle(!setShowInfoToggle)

//search filter function
const searchFn =(e)=>{
    const searchWords = e.target.value.toLowerCase()//.split(" ").join("")
    const reg = new RegExp(searchWords);
    //test with regex
 let newB = newData.filter(x=> reg.test(x.name.toLowerCase()))

if(e.target.value === ""){
    setInfo(newData)  } 

        if (newB.length > 1){
            setInfo(newB)
        }
}

//state storage input function 
const handleChange = (e)=>{
    const {id, value} = e.target
if(id=== "firstName"){
setFirstName(value)
}
if(id === "lastName"){
    setLastName(value)
}
if(id === "email"){
    setEmail(value)
}
if(id === "phone"){
    setPhone(value)
}
if(id === "radio1"){
    setSex("Male")
}
if(id === "radio2"){
    setSex("Female")
}
}

    return (
    <div className='admin-page'>
<header>
    <input onKeyUp={(e)=> searchFn(e)}type="input" className="search" id="search" placeholder="Enter full name to search" />
  
    <div className={`${mobileMenuToggle ? "hamburger active" : "hamburger"}`} onClick={()=>setMobileMenuToggle(!mobileMenuToggle)}>
<div className="bar 1"></div>
<div className="bar 2"></div>
<div className="bar 3"></div>
</div>  
  
</header>

<div className={`${mobileMenuToggle ? "utility-section active" : "utility-section"}`}>
    <button className="utility-btn" onClick={()=>addBtnClick()}>Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">

{/*add page display */}
{addInfoDisplayToggle && 
    <AddPage 
    addBtn={(e)=>addBtn(e)}
    handleChange={()=>handleChange()}
  firstName={firstName}
 lastName={lastName}
  email={email}
  phone={phone}
  sex={sex}
    /> }

{/*personal info full display page */}
    {showInfoToggle && <Detailspage 
    name={name}
    sex={sexy}
    handleClick={closeInfoBtn}
    />}

    {isPending && <div className="loader"></div>}

{info &&
<div className="main-content">
<table>
<thead>

        <tr className='person'>
        <th>N/0</th>
        <th>Name</th>
        <th>Sex</th>
        <th>View Info</th>
        </tr>
</thead>
<tbody>
    {info.map((x,y)=>{
       let styles ={ padding:"0px",} 


       return(         
 <tr className='person' key={x.id}>
        <td className='number'>{x.id}</td>
        <td className="p-name">{x.firstName}{x.name} {x.lastName}</td>
        <td className='sex'>{x.sex}</td>
        <td style={styles}>
       <button className="more-btn" id={x.id} onClick={(e)=>showInfoBtn(e)}>
                More..
</button>
</td>
  </tr>
)

    })}
    </tbody>
    </table>
    </div> }
    </div> 
 
    </div>
   
)

};
  /* {showInfo && <div className="modal-container">
 <div className="Personal-info-full-display">
           <p>Full Name:{x.name}</p>
           <p>sex:{x.sex}</p>
<div className="btn-container">
<button onClick={()=> props.handleClick()}className="proceed-btn">Close</button>
</div>
</div>
</div> 
}*/
export default Admin;
