import { useState } from 'react';
import './Admin.css';

const data =[
{id:1,name:"Funsho ajayi",sex:"M",},
{id:2,name:"Funsho ajayi",sex:"M",},
{id:3,name:"victor Kiolade",sex:"F",},
{id:4,name:"Mudia baudnsn",sex:"M",},
{id:5,name:"victor Kiolade",sex:"M",},
{id:6,name:"Mudia baudnsn",sex:"F",},
]
//if(name.indexOf(word) === 0 )



const Admin = () => {
    const [info, setInfo] = useState(data)
   let newB ;


const searchFn =(e)=>{
   const word = e.target.value.toLowerCase(); 
    let db = info.slice()
 if(word === ""){
    setInfo(db)  } 
   
newB = db.filter((x)=> x.name == e.target.value.toLowerCase())
 let xx = db.filter(x=> console.log(e.target.value.toLowerCase()))
//x.name.toLowerCase().split(" ").join("")
}

    return (<div className='admin-page'>
<header>
    <input onKeyUp={(e)=> searchFn(e)}type="input" className="search" id="search" placeholder="Search data base" />
</header>

<div className="utility-section">
    <button className="utility-btn">Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">
<div className="main-content">
<table>
<thead>
    
        <tr className='person'>
        <th>N/0</th>
        <th>Name</th>
        <th>Sex</th>
        <th>View Info</th>
        </tr>
</thead>
<tbody>
    {info.map((x)=>{
       let styles ={ padding:"0px",} 
       return(         
 <tr className='person' key={x.id}>
        <td className='number'>{x.id}</td>
        <td className="p-name">{x.name}</td>
        <td className='sex'>{x.sex}</td>
        <td style={styles}><button className="more-btn">More...</button></td>
  </tr>
        )

    })}
    </tbody>
    </table>
    </div> 
    </div>
    </div>)

}


export default Admin;
