import { useState } from "react"
import UpdateInfoPage from "./UpdateInfoPage"


const Detailspage = (props) => {
  //update person info page toggle 
const [updateInfoPageToggle, setUpdateInfoPageToggle] = useState(false)

//states for storing new data to be pushed to the backend storage
const [firstName, setFirstName] = useState("")
const [lastName, setLastName] = useState("")
const [email, setEmail] = useState("")
const [phone, setPhone] = useState("")

const closeUpdatePage = ()=> setUpdateInfoPageToggle(false)

return (<>
{/* update person info page */}
{updateInfoPageToggle &&
<UpdateInfoPage 
updateBtn={(e)=>props.updateClick(e)}
closeAddBtn={()=>closeUpdatePage()}
handleChange={(e)=>handleChange(e)}
firstName={props.firstName}
lastName={props.lastName}
email={props.email}
phone={props.phone}
/>
}
            <div className="details-Page">
                <button onClick={()=> props.closeClick()}>X</button>
              <h3>{props.firstName} {props.lastName}</h3>
               <div className="details-page-content">
               <p>First Name : {props.firstName}</p>
               <p>Last Name : {props.lastName}</p>
               <p>Email : {props.email}</p>
               <p>Phone number : {props.phone}</p>
               <p>Sex : {props.sex}</p>
    <div className="btn-container">
   <button onClick={(e)=> setUpdateInfoPageToggle(true)} className="proceed-btn"  id={props.id}>Update Data</button> 
    <button onClick={(e)=> props.deleteClick(e)} className="delete-data-btn" id={props.id}>Delete Data</button>
    </div>
    </div>
    </div>
    </>
          );
    }
     
    
    export default Detailspage;
