import { useState } from 'react';
import './Admin.css';
import { Link } from "react-router-dom";
//import Detailspage from "./Detailspage";
//import { BrowserRouter as Router,Route, Switch } from "react-router-dom";
import { initializeApp } from "firebase/app";
import { getFirestore, collection, getDocs } from 'firebase/firestore';

const firebaseConfig = {
    apiKey: "AIzaSyD0oBLm3VYkH-CYmNgkuMjd-UUtW4Qr4p8",
    authDomain: "registration-db-723e3.firebaseapp.com",
    projectId: "registration-db-723e3",
    storageBucket: "registration-db-723e3.appspot.com",
    messagingSenderId: "751124590289",
    appId: "1:751124590289:web:4340c9c1cf7b5c392d1ff0"
  };

  //init firebase app
initializeApp(firebaseConfig);

//init services
const db = getFirestore()

//collection ref
const colRef = collection(db, "Information")

//get collection data
getDocs(colRef)
.then((snapshot)=>{
    console.log(snapshot.docs)
})


const data =[
{id:1,name:"Funsho ajayi",sex:"M",},
{id:2,name:"Funsho ajayi",sex:"M",},
{id:3,name:"victor Kiolade",sex:"F",},
{id:4,name:"Mudia baudnsn",sex:"M",},
{id:5,name:"victor Kiolade",sex:"M",},
{id:6,name:"Mudia baudnsn",sex:"F",},
]

const Admin = () => {
    const [info, setInfo] = useState(data)
   let newB ;


   const newData = data.slice()
const searchFn =(e)=>{
    //compare exact match since idk how to use regex yet
 let newB = newData.filter(x=> x.name.toLowerCase().split(" ").join("") === e.target.value.toLowerCase().split(" ").join(""))

if(e.target.value === ""){
    setInfo(newData)  } 

        if (newB.length > 1){
            setInfo(newB)
        }
        //let name = e.target.value.toLowerCase().split(" ").join("")
       //const reg = new RegExp('\b\w*name\w*\b', 'i');
      // console.log(reg.test("funsho"))
}
    return (
    
    <div className='admin-page'>
<header>
    <input onKeyUp={(e)=> searchFn(e)}type="input" className="search" id="search" placeholder="Enter full name to search" />
</header>

<div className="utility-section">
    <button className="utility-btn">Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">
<div className="main-content">
<table>
<thead>

        <tr className='person'>
        <th>N/0</th>
        <th>Name</th>
        <th>Sex</th>
        <th>View Info</th>
        </tr>
</thead>
<tbody>
    {info.map((x)=>{
       let styles ={ padding:"0px",} 
       return(         
 <tr className='person' key={x.id}>
        <td className='number'>{x.id}</td>
        <td className="p-name">{x.name}</td>
        <td className='sex'>{x.sex}</td>
        <td style={styles}>
            <button className="more-btn">
                More..
                </button></td>
  </tr>
        )

    })}
    </tbody>
    </table>
    </div> 
    </div>
    </div>
)

}


export default Admin;
