{id:5,name:"Mudia baudnsn",sex:"F",},
]

const searchFn =(e)=>{
    let word = e.target.value.toLowerCase();
const newB = info.filter(x=>{
    if(x.name.toLowerCase().indexOf(word) !== -1 )
{
  setsearch(newB)  
  console.log(newB)
