</header>

<div className="utility-section">
    <button className="utility-btn">Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">
<div className="main-content">

    {info.map((x,y)=>{
        return(   
<button className='person' id="{x.id}">
