import { useState } from 'react';
import './Admin.css';
import { Link } from "react-router-dom";
import Detailspage from "./Detailspage";
import { BrowserRouter as Router,Route, Switch } from "react-router-dom"

const data =[
{id:1,name:"Funsho ajayi",sex:"M",},
{id:2,name:"Funsho ajayi",sex:"M",},
{id:3,name:"victor Kiolade",sex:"F",},
{id:4,name:"Mudia baudnsn",sex:"M",},
{id:5,name:"victor Kiolade",sex:"M",},
{id:6,name:"Mudia baudnsn",sex:"F",},
]
//if(name.indexOf(word) === 0 )



const Admin = () => {
    const [info, setInfo] = useState(data)
   let newB ;

   const db = data
const searchFn =(e)=>{
    //compare exact match since idk how to use regex yet
 let newB = db.filter(x=> x.name.toLowerCase().split(" ").join("") === e.target.value.toLowerCase().split(" ").join(""))

if(e.target.value === ""){
    setInfo(db)  } 

        if (newB.length > 1){
            setInfo(newB)
        }
        console.log(newB)
        console.log(info)
        console.log(e.target.value === "")
        console.log(db)
}
    return (
    
<Router>
    <div className='admin-page'>
<header>
    <input onKeyUp={(e)=> searchFn(e)}type="input" className="search" id="search" placeholder="Enter full name to search" />
</header>

<div className="utility-section">
    <button className="utility-btn">Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">
<div className="main-content">
<table>
<thead>

        <tr className='person'>
        <th>N/0</th>
        <th>Name</th>
        <th>Sex</th>
        <th>View Info</th>
        </tr>
</thead>
<tbody>
    {info.map((x)=>{
       let styles ={ padding:"0px",} 
       return(         
 <tr className='person' key={x.id}>
        <td className='number'>{x.id}</td>
        <td className="p-name">{x.name}</td>
        <td className='sex'>{x.sex}</td>
        <td style={styles}>
           <Link to='/Detailspage'> <button className="more-btn">
                More..
                </button></Link></td>
  </tr>
        )

    })}
    </tbody>
    </table>
    </div> 
    </div>
    </div>
    </Router>)

}


export default Admin;
