import { useState } from "react";
import Welcome from "./Welcome";
const Form = () => {
    const [click, setclick] = useState(true)

    const remove =()=>setclick(!click)
    //{setTimeout(()=>(),2000)}


    return (  
        <div>

{click && <Welcome handleClick={remove}/>}
<div>
        <div className="regform">
             <h1>Registration form</h1>
    <div className="main">
       
    <form> 
        <div id="name">
<input className="name" type="text" name="Firstname" placeholder="first name" required />
<input className="name" type="text" name="Lastname" placeholder="last name" required />
        </div>	
    
    <input className="email" type="email" placeholder="EMAIL" required /><br />
    
    <input className="email" type="phone" placeholder="Phone number" required /><br />


    <label className="radio">
        <input className="radio-one" type="radio" name="radio" />
    Male</label>
    
    <label className="radio">
        <input className="radio-two" type="radio" name="radio" />
   Female</label>
        
    <button type="submit">Register</button>
        </form>
        </div>
        </div>
        </div>
      
        </div>
  
    );
}

 
export default Form;
