//import Detailspage from "./Detailspage";
//import { BrowserRouter as Router,Route, Switch } from "react-router-dom";
import { db } from "./firebaseConfig";
import { collection, getDocs , addDoc, deleteDoc, doc } from "firebase/firestore"
import { async } from '@firebase/util';
 //import { async } from '@firebase/util';


const data =[
{id:1,name:"Funsho ajayi",sex:"M",},
{id:2,name:"Funsho ajayi",sex:"M",},
{id:3,name:"victor Kiolade",sex:"F",},
{id:4,name:"Mudia baudnsn",sex:"M",},
{id:5,name:"victor Kiolade",sex:"M",},
{id:6,name:"Mudia baudnsn",sex:"F",},
]

//getting data from database
const regCollection = collection(db, "Information ");


const Admin = () => {
//first things first
//does this onpage load    
const firebaseData = []
useEffect(()  => {

 getDocs(regCollection)
    .then((snap)=>{
        snap.docs.forEach((doc) => firebaseData.push({...doc.data() , id:doc.id}))
      setIsPending(false)
    setInfo(data)
          })
    .catch(err=>alert(err.message))
        

    }, [])

 console.log(firebaseData)


//states
    const [info, setInfo] = useState(null)
    const [isPending, setIsPending] = useState(true)
    const [showInfo, setShowInfo] = useState(false)
     const [click, setclick] = useState(false)
     const [firstName, setFirstName] = useState("")
     const [lastName, setLastName] = useState("")
      const [sex, setSex] = useState("")
     const [email, setEmail] = useState("")
     const [phone, setPhone] = useState("")

     

   let newB ;


   
//Manual add buttton, addBtn function
const addBtn =(e)=> {
e.preventDefault()
  
    let obj ={
        firstName:firstName,
        lastName:lastName,
        sex:sex,
        email:email,
        phone:phone,
    }  
  console.log(obj)
  
  if((obj.firstName !== null || "") && (obj.lastName !== null || "")  && (obj.email !== null  || "") && 
  (obj.phone !== null || ""))
  { addDoc(regCollection, obj)
    .then(()=>{
  if((obj.firstName !== null || "") && (obj.lastName !== null || "")  && (obj.email !== null  || "") && 
  (obj.phone !== null || ""))
  {alert("Data was added succesfully")
}
})
.catch((error)=> {alert("There was an error :"+ error)})
}

    
    addBtnClick()
}

//addBtn click
const addBtnClick =(e)=> setclick(!click)

//making a backup copy of the intial demo database info
const newData = data.slice()

//search filter function
const searchFn =(e)=>{
    //compare exact match since idk how to use regex yet
 let newB = newData.filter(x=> x.name.toLowerCase().split(" ").join("") === e.target.value.toLowerCase().split(" ").join(""))

if(e.target.value === ""){
    setInfo(newData)  } 

        if (newB.length > 1){
            setInfo(newB)
        }
        //let name = e.target.value.toLowerCase().split(" ").join("")
       //const reg = new RegExp('\b\w*name\w*\b', 'i');
      // console.log(reg.test("funsho"))
}

//state storage input function 
const handleChange = (e)=>{
    const {id, value} = e.target
if(id=== "firstName"){
setFirstName(value)
}
if(id === "lastName"){
    setLastName(value)
}
if(id === "email"){
    setEmail(value)
}
if(id === "phone"){
    setPhone(value)
}
if(id === "radio1"){
    setSex("Male")
}
if(id === "radio2"){
    setSex("Female")
}
}


//function for veiwing info per person
const veiwInfo = (e)=>{

}

    return (
    <div className='admin-page'>
<header>
    <input onKeyUp={(e)=> searchFn(e)}type="input" className="search" id="search" placeholder="Enter full name to search" />
</header>

<div className="utility-section">
    <button className="utility-btn" onClick={()=>addBtnClick()}>Add data</button>
    <button className="utility-btn">Refresh</button> 
    <button className="utility-btn">Change Password</button>
    <button className="utility-btn">Logout</button>
</div>
<div className="body">

{/*add page display */}
{click && <div className="modal-container">
    <AddPage 
    addBtn={(e)=>addBtn(e)}
    handleChange={(e)=>handleChange(e)}
  firstName={firstName}
 lastName={lastName}
  email={email}
  phone={phone}
  sex={sex}
    />
    </div> }

    {isPending && <div className="loader"></div>}

{info &&
<div className="main-content">
<table>
<thead>

        <tr className='person'>
        <th>N/0</th>
        <th>Name</th>
        <th>Sex</th>
        <th>View Info</th>
        </tr>
</thead>
<tbody>
    {info.map((x,y)=>{
       let styles ={ padding:"0px",} 

       //function for veiwing info per person
const veiwInfo = (e)=>{
setShowInfo(!showInfo)
}
       return(         
 <tr className='person' key={x.id}>
        <td className='number'>{y}</td>
        <td className="p-name">{x.firstName}{x.name} {x.lastName}</td>
        <td className='sex'>{x.sex}</td>
        <td style={styles}>
            <button className="more-btn" onClick={(e)=>veiwInfo(e)}>
                More..
           
</button>
</td>
  </tr>
  
   /*  {showInfo && <div className="modal-container">
 <div className="Personal-info-full-display">
           <p>Full Name:{x.name}</p>
           <p>sex:{x.sex}</p>
<div className="btn-container">
<button onClick={()=> props.handleClick()}className="proceed-btn">Close</button>
</div>
</div> }*/

        )

    })}
    </tbody>
    </table>
    </div> }
    </div>
    </div>
)

};

export default Admin;
